import React from 'react';

const EpisodeInfo = (props) => {

    async function getCharAsync(url)
    {
      let response = await fetch(url);
      let result = await response.json()
      return result;
    }

    const {name, air_date, episode, characters} = props.episode;

    // const charNames = characters.forEach(c => getCharAsync(c)
    // .then(data => ({id: result.id, name: result.name, url: c})))

    const charNames = characters.forEach(char => {
        getCharAsync(char)
      .then(result => ({name: result.name}))});


    // {console.log(charNames)}

    // characters.forEach((c) =>  fetch(c)
    //                             .then(response => response.json())
    //                             .then(result => charNames.push({id: result.id, name: result.name, url: c})))

    // const charNames =   characters.forEach((c) =>  fetch(c)
    //                         .then(response => response.json())
    //                         .then(result => ({id: result.id, name: result.name, url: c})));

    function handle_click (e) {
        e.preventDefault();

        props.searchData("character_id", "1", "10");
    }
    // const listItems = charNames.map((a) =>  <li>{a}</li>);
    return (
        <div className="row">
            <div className="col-4">
                <div className="card">
                    <div className="card-body">
                        <p className="card-text">Name: {name} </p>
                        <p className="card-text">Date: {air_date} </p>
                        <p className="card-text">Code: {episode} </p>
                    </div>
                </div>
            </div>
            <div className="col-8">
                <div className="card">
                    <div className="card-body">
                        {/* {console.log(characters)} */}
                        {/* {console.log(charNames[0])} */}
                        {console.log(charNames)}
                        {/* {console.log(characters)} */}
                        <p className="card-text">Characters: </p>
                        <ul>
                            {characters.map(c =>
                                <li><p className="card-text "> {c.name} </p></li>
                            )}
                            {/* <li><a onClick={handle_click}>
                                Click me! </a></li> */}
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default EpisodeInfo;
