import React from 'react';

const EpisodeInfo = (props) => {


    const {name, air_date, episode, characters} = props.episode;
    const charnames = ["S01E01", "S01E02"];

    characters.map((c) => charnames.push(c))
    // characters.map((c) =>
    //                     fetch(c)
    //                     .then(response => response.json())
    //                     .then(data => data.name ))
    //       // We get the API response and receive data in JSON format...
          // ...then we update the users state

    return (
        <div className="row">
            <div className="col-4">
                <div className="card">
                    <div className="card-body">
                        <p className="card-text">Name: {name} </p>
                        <p className="card-text">Date: {air_date} </p>
                        <p className="card-text">Code: {episode} </p>
                    </div>
                </div>
            </div>
            <div className="col-8">
                <div className="card">
                    <div className="card-body">
                        {console.log(charnames)}
                        <p className="card-text">Characters: </p>
                        <ul>
                        {characters.map(c =>
                                <li><a href={name} className="card-text"> {c.value} </a></li>
                            )}
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default EpisodeInfo;
