import React from 'react';

const EpisodeInfo = (props) => {

    const {name, air_date, episode, characters} = props.episode;
    const charNames = [];
    characters.map((c) =>  fetch(c)
                            .then(response => response.json())
                            .then(data => charNames.push({name: data.name, url: c})))
    const listItems = charNames.map((a) =>  <li>{a}</li>);

    return (
        <div className="row">
            <div className="col-4">
                <div className="card">
                    <div className="card-body">
                        <p className="card-text">Name: {name} </p>
                        <p className="card-text">Date: {air_date} </p>
                        <p className="card-text">Code: {episode} </p>
                    </div>
                </div>
            </div>
            <div className="col-8">
                <div className="card">
                    <div className="card-body">
                        {console.log(charNames)}
                        {console.log(listItems)}
                        <p className="card-text">Characters: </p>
                        <ul>
                            {listItems}
                        {/* {charNames.map(c =>
                                <li><a href={"google.cl"} className="card-text"> {c.name} </a></li> */}
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default EpisodeInfo;
